<div class="pagina-carrito">
    <h3 class="titulo-principal">Carrito:</h3>

    <form class="layout-carrito" method="POST" action="index.php?controller=carrito&action=pagar">

        <!-- IZQUIERDA: Productos + Cliente -->
        <div class="col-izquierda">
            <!-- Productos -->
            <div class="carrito-pagina">
                <div id="carritoPaginaContenido"></div>
                <div class="footer-carrito">
                    <a href="?controller=Carta&action=index" class="seguir-comprando">Seguir Comprando</a>
                </div>
            </div>

            <!-- Datos Cliente -->
            <section class="datos-cliente-seccion"> 
                <h4 class="titulo-datos-cliente">Datos Cliente:</h4> 
                <div class="formulario-dos-columnas">
                    <div class="grid-datos">
                        <div>
                            <label for="nombre">Nombre:</label>
                            <input type="text" id="nombre" name="nombre" required>
                        </div>

                        <div>
                            <label for="correo">Correo:</label>
                            <input type="email" id="correo" name="correo" required>
                        </div>

                        <div>
                            <label for="apellido">Apellido:</label>
                            <input type="text" id="apellido" name="apellido" required>
                        </div>

                        <div>
                            <label for="telefono">Teléfono:</label>
                            <input type="tel" id="telefono" name="telefono" required>
                        </div>

                        <div class="direccion">
                            <label for="direccion">Dirección:</label>
                            <input type="text" id="direccion" name="direccion" required>
                        </div>

                        <div class="codigoPostal">
                            <label for="codigoPostal">Código Postal:</label>
                            <input type="text" id="codigoPostal" name="codigoPostal" required>
                        </div>
                    </div>
                </div>
            </section>
        </div>

        <!-- DERECHA: Resumen -->
        <div class="col-derecha">
            <div class="resumen">
                <div class="titulo-resumen">
                    <h3>RESUMEN</h3>
                </div>

                <label class="resumen-label">Num.Tarjeta:</label>
                <input type="number">

                <label class="resumen-label">Titular de la Tarjeta:</label>
                <input type="text">

                <div class="fila">
                    <div>
                        <label class="resumen-label">Fecha Caducidad:</label>
                        <input type="text">
                    </div>
                    <div>
                        <label class="resumen-label">CVC:</label>
                        <input type="number">
                    </div>
                </div>

                <hr>

                <div class="totales">
                    <span>SubTotal:</span>
                    <strong id="totalCarritoPagina"></strong>
                </div>

                <hr>

                <div class="totales">
                    <span>Precio_total:<br>(iva incluido)</span>
                    <strong id="totalFinal">15€</strong>
                </div>
                <!-- Input hidden para enviar carrito -->
                <input type="hidden" name="carrito" id="carritoInput">
                <button class="btn-pagar" type="submit">Pagar Ahora</button>

                <div>
                    <p class="disponible">Disponible:</p>
                </div>

                <div class="metodos">
